  var row = yield* Tag.findByNameAndTag(name, tag);
  if (!row) {
    row = Tag.build({
      name: name,
      tag: tag
    });
  }
  row.module_id = mod.id;
  row.version = version;
  if (row.changed()) {
    return yield row.save();
  }
  return row;
};

exports.getModuleTag = function* (name, tag) {
