      {
        fields: ['gmt_modified']
      }
    ],
    classMethods: {
      findByName: function* (name) {
        return yield this.find({
          where: {
            name: name
          }
        });
      },
      save: function* (name, pkg) {
        var row = yield this.find({
          where: {
            name: name
          }
        });
        if (row) {
          row.package = pkg;
          if (row.changed()) {
            row = yield row.save(['package']);
          }
          return row;
        }

        row = this.build({
          name: name,
          package: pkg,
        });
